package com.tydic.listener;import java.io.IOException;import javax.servlet.ServletContextEvent;import javax.servlet.ServletContextListener;import com.tydic.util.ConfigUtil;/** * 配置监听器,对sysconf.properties的加载<br/> * <b>加载顺序在spring容器加载前</b> * @author leo * */public class ConfigListener implements ServletContextListener {	private static final String SYS_CONFIG_REF_TIME = "sysConfigRefTime";	private static final String SYS_CONFIG_LOCATION = "sysConfigLocation";	public void contextDestroyed(ServletContextEvent arg0) {	}	public void contextInitialized(ServletContextEvent arg0) {		String sysconfig = arg0.getServletContext().getInitParameter(				SYS_CONFIG_LOCATION);		long sysConfigRefTime = Long.valueOf(arg0.getServletContext()				.getInitParameter(SYS_CONFIG_REF_TIME));		try {			ConfigUtil.init(sysconfig, sysConfigRefTime);		} catch (IOException e) {			e.printStackTrace();		}	}}